// reference: https://react-bootstrap.github.io/components/modal/

import { useState } from "react";
import { Button, Modal, Form } from "react-bootstrap";
import "../styles/modal.css";

const SignInModal = () => {
  const [show, setShow] = useState(true); // show sign-in
  const [showSignUp, setShowSignUp] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  let data = { email: email, password: password };

  const handleSignIn = () => {
    setEmail("");
    setPassword("");
    console.log(data);
  };

  return (
    <Modal
      dialogClassName={"my-modal"}
      centered
      show={show}
      onHide={handleClose}
    >
      <Modal.Header className="px-4" closeButton>
        <Modal.Title className="ms-auto">Sign in to your account</Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form id="modal-form">
          <Form.Group className="me-3">
            <Form.Label> Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="email@example.com"
              autoFocus
              value={email}
              onChange={(event) => setEmail(event.target.value)}
            />
          </Form.Group>
          <Form.Group id="password-signUp">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(event) => setPassword(event.target.value)}
            />
          </Form.Group>
        </Form>
      </Modal.Body>

      <div className="modal-button-container">
        <Button className="modal-button" type="submit" onClick={handleSignIn}>
          Sign In
        </Button>
      </div>

      <div id="modal-footer-container">
        <p>Dont' have an account?</p>
        <a>Forget password?</a>
      </div>
    </Modal>
  );
};

export default SignInModal;
