// reference: https://react-bootstrap.github.io/components/modal/
// reference: https://getbootstrap.com/docs/5.3/components/buttons/
// 3. https://mdbootstrap.com/docs/react/utilities/spacing/
// 4. https://react-bootstrap-v3.netlify.app/components/forms/

import { useState } from "react";
import { Button, Modal, Form, InputGroup, FormControl } from "react-bootstrap";
import "../styles/modal.css";
import { FaEye, FaEyeSlash } from "react-icons/fa";

const SignInModal = ({ isShow }) => {
  const [show, setShow] = useState(isShow); // show sign-in
  const [showSignUp, setShowSignUp] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [revealed, setRevealed] = useState(false);

  const handleClose = () => setShow(false);

  let data = { email: email, password: password };

  const emailValidation = {};

  const handleSignIn = () => {
    console.log(data);
  };

  const isValidEmail = (str) => {
    if (str.length != 0) {
      let reg = /^([a-zA-Z0-9_-])+@([a-zA-Z0-9_-])+(.[a-zA-Z0-9_-])+/;
      return reg.test(str) ? true : false;
    }
    return false;
  };

  const handlePassword = () => {
    setRevealed(!revealed);
  };

  return (
    <Modal
      dialogClassName={"my-modal"}
      //centered
      show={show}
      onHide={handleClose}
      className="modal"
    >
      <Modal.Header className="px-4" closeButton>
        <Modal.Title className="ms-auto">
          <b>Sign in to your account</b>
        </Modal.Title>
      </Modal.Header>

      <Modal.Body>
        <Form id="modal-form">
          <Form.Group id="email-signUp">
            <Form.Label> Email</Form.Label>
            <Form.Control
              type="email"
              placeholder="email@example.com"
              autoFocus
              value={email}
              required
              onChange={(event) => setEmail(event.target.value)}
            />
          </Form.Group>

          <Form.Group id="password-signUp">
            <Form.Label>Password</Form.Label>
            <InputGroup className="mb-3">
              <Form.Control
                type={revealed ? "text" : "password"}
                value={password}
                placeholder="password"
                required
                onChange={(event) => setPassword(event.target.value)}
              />
              <Button variant="outline-secondary" onClick={handlePassword}>
                {revealed ? <FaEyeSlash /> : <FaEye />}
              </Button>
            </InputGroup>
          </Form.Group>

          <Button id="modal-button" type="submit" onClick={handleSignIn}>
            Sign In
          </Button>
        </Form>
      </Modal.Body>

      <div id="modal-footer-container">
        <p>
          Dont' have an account?<a href=""> Sign Up</a>
        </p>
        <a href="">Forget password?</a>
      </div>
    </Modal>
  );
};

export default SignInModal;
